jQuery(document).ready(function () {
    jQuery("#color-picker").on('change', function () {
        jQuery("input[name=bk_color]").val(jQuery(this).val());
    });
    var spnr_val = jQuery('#edit-sponsored-category-yes').is(':checked');
    color_picker_condition(spnr_val);
    jQuery('#edit-sponsored-category-yes').on('click', function () {
        var spnr_val = jQuery('#edit-sponsored-category-yes').is(':checked');
        color_picker_condition(spnr_val);
    });
});

function color_picker_condition(spnr_val) {
    if (spnr_val) {
        jQuery('.form-item-bk-color').show();
        jQuery('#color-picker').show();
    } else {
        jQuery('.form-item-bk-color').hide();
        jQuery('#color-picker').hide();
    }
}