jQuery(document).ready(function () {
    jQuery('body').mousemove(function () {
        var color_picker_value = jQuery("#color-picker").val();
        jQuery("input[name=bk_color]").attr('value', color_picker_value);
    });
    var spnr_val = jQuery("input[name='sponsored_category[Yes]']").is(':checked');
    color_picker_condition(spnr_val);
    jQuery('#edit-sponsored-category-yes').on('click', function () {
        var spnr_val = jQuery("input[name='sponsored_category[Yes]']").is(':checked');
        color_picker_condition(spnr_val);
    });
});

function color_picker_condition(spnr_val) {
    if (spnr_val) {
        jQuery('.form-item-bk-color').css("display", "block");
        jQuery('#color-picker').css("display", "block");
    } else {
        jQuery('.form-item-bk-color').css("display", "none");
        jQuery('#color-picker').css("display", "none");
    }
    jQuery("label[for=edit-category-icon-upload]").html('Upload External Menu Icon <span class="form-required" title="This field is required.">*</span>');
}